    @GetMapping("downfiles")
    public ResponseEntity<Resource> downloadFile() {
        String folderPath = "/Users/songyongmeng/IdeaProjects/test"; // 指定文件夹的路径
        File folder = new File(folderPath);
        File[] files = folder.listFiles();
        String fileName = "";
        if (files != null) {
            for (File file : files) {
                if (file.isFile()) {
                    fileName = file.getName();
                }
            }
        }


        try {
            File file = new File(folderPath, fileName);
            FileInputStream fis = new FileInputStream(file);

            // 创建输入流资源
            InputStreamResource resource = new InputStreamResource(fis);

            // 设置响应头
            HttpHeaders headers = new HttpHeaders();
            headers.add(HttpHeaders.CONTENT_DISPOSITION, "attachment; filename=" + new String(fileName.getBytes(StandardCharsets.UTF_8), StandardCharsets.ISO_8859_1));

            // 返回响应实体
            return ResponseEntity.ok()
                    .headers(headers)
                    .contentType(MediaType.APPLICATION_OCTET_STREAM)
                    .body(resource);
        } catch (IOException e) {
            // 处理文件读取错误
            return ResponseEntity.notFound().build();
        }

    }



