    public static boolean isBlank(CharSequence cs) {
        int strLen;
        if (cs != null && (strLen = cs.length()) != 0) {
            for (int i = 0; i < strLen; ++i) {
                if (!Character.isWhitespace(cs.charAt(i))) {
                    return false;
                }
            }

            return true;
        } else {
            return true;
        }
    }

    private static void deleteAction(String folderPath){
        File folder = new File(folderPath);
        if (!folder.exists() || !folder.isDirectory()) {
            System.out.println("指定路径不是一个有效的文件夹。");
            return;
        }

        File[] files = folder.listFiles();
        if (files != null) {
            for (File file : files) {
                if (file.isFile()) {
                    if (file.delete()) {
                        System.out.println("已删除文件: " + file.getAbsolutePath());
                    } else {
                        System.out.println("无法删除文件: " + file.getAbsolutePath());
                    }
                } else if (file.isDirectory()) {
                    deleteFolder(file);
                }
            }
        }

        System.out.println("文件夹已清空，文件夹保留。");
    }

    private static void deleteFolder(File folder) {
        File[] files = folder.listFiles();
        if (files != null) {
            for (File file : files) {
                if (file.isFile()) {
                    if (file.delete()) {
                        System.out.println("已删除文件: " + file.getAbsolutePath());
                    } else {
                        System.out.println("无法删除文件: " + file.getAbsolutePath());
                    }
                } else if (file.isDirectory()) {
                    deleteFolder(file);
                }
            }
        }
        folder.delete();
        System.out.println("已删除文件夹: " + folder.getAbsolutePath());
    }
